Session = function (sessionid, userid, sessiontimestamp) {
    var _that = this;
    
    var sessionID = sessionid;
    var userID = userid;
    // milisek till expire
    var sessionTimestamp = sessiontimestamp;

    this.getSessionID = function () {
        if (sessionID && sessionID != 0) {
            return sessionID;
        }
        else {
            return 'S: ERROR sessionID is undefined, null or 0';
        }
    };
    
    this.getUserID = function () {
        if (userID && userID != 0) {
            return userID;
        }
        else {
            return 'S: ERROR userID is undefined, null or 0';
        }
    };
    
    this.getSessionTimestamp = function () {
        if (sessionTimestamp && sessionTimestamp != 0) {
            return sessionTimestamp;
        }
        else {
            return 'S: ERROR sessionTimestamp is undefined, null or 0';
        }
    };
    
    this.setSessionTimestamp = function (timestamp) {
        if (timestamp) {
            sessionTimestamp = timestamp;
        }
        else {
            return 'S: ERROR timestamp is undefined or null';
        }
    };
};

